<script>
export default {
  layout: 'auth',
  modal: 'input',
  props: {
    role: {
      type: String,
      required: false,
      default: 'BUYER',
    },
  },
  data() {
    return {
      userType: this.role,
    }
  },
  watch: {
    userType(v) {
      this.$emit('input', this.userType)
    },
  },
}
</script>
